/**
 * @typedef {string|number|Object|boolean} ProxyValue - Emits the scoped event when this property changes.
 */

/**
 * @typedef {Object} ComponentData
 * @property {string} id - The component class name, used by the {@link ResourceProvider}
 *  to pull the component class.
 * @property {*} [*] - Any data.
 */

/**
 * @typedef {ComponentData} Component
 */

/**
 * @typedef {Object} EntityData
 * @property {string} [_primaryMesh] - Optional ID of component that holds entity's
 *  primary mesh object.
 * @property {ComponentData} [*] - Component properties.
 * @example <caption>ID: 'objects.sphere'</caption>
 * objects = {
 *     sphere: {
 *         _primaryMesh: 'MyMeshComponent',
 *
 *         MyMeshComponent: {position: {x: 0, y: 5, z: 0}},
 *         MyLogicComponent: {value: 'foobar'}
 *     }
 * };
 */

/**
 * @typedef {EntityData} EntityMetaData
 * @property {string} id - The generic Entity ID, used by the {@link ResourceProvider}
 *  to pull the data.
 * @property {string} uid - the instance-specific identifier.
 * @example <caption>Basic Definition</caption>
 * let entityData1 = {id: 'objects.sphere', uid: 'myFirstSphere'};
 * @example <caption>Advanced Definition</caption>
 * let entityData2 = {
 *     id: 'objects.sphere',
 *     uid: 'mySecondSphere',
 *     MyMeshComponent: {rotation: {y: Math.PI},
 *     MyLogicComponent: {value: 42} // This will override the default definition.
 * };
 */

 /**
 * @typedef {Object} EntityInstance
 * @property {string} id - The Entity ID.
 * @property {string} uid - The unique identifier for the entity instance.
 * @property {Component|undefined} mesh - The component that holds the primary mesh
 *  object for the entity.
 * @property {function(engine: Engine, time: number, deltaTime: number): undefined} tick -
 *  Executes all registered component tick methods.
 * @property {Promise<Component>} meshAsync - Wait for entity's mesh component to be
 *  set and get the value, asynchronously.
 */

/**
 * @typedef {class} ComponentClass - A static class for a component
 * @example <caption>Basic Logic Component</caption>
 * class MyLogicComponent {
 *     constructor(engine, entity, id, {value}) {
 *         this.id = id;
 *         this.value = value;
 *         this.enabled = true;
 *         entity.tick = {id, tick: MyLogicComponent.tick};
 *         return comp;
 *     }
 *     disableMe() {
 *         this.enabled = false;
 *     }
 *     static update(engine, entity, comp, id, data) {
 *         if (!comp) {
 *             comp = new MyLogicComponent(engine, entity, id, data);
 *         } else {
 *             comp.value = data.value;
 *         }
 *         return comp;
 *     }
 *     static dismount(engine, entity, comp) {
 *         delete comp.value;
 *         return null;
 *     }
 *     static tick(engine, entity, comp, t, dt) {
 *         if (comp.enabled) {
 *             engine.ctrl.target.AnotherComponent.doSomething(comp.value);
 *         }
 *     }
 * }
 * @example <caption>BabylonJS Object Wrapper</caption>
 * class MyMeshComponent {
 *     static create(engine, entity, id, meshData) {
 *         const comp = {id, baby: null};
 *
 *         return someFancyAsyncAction().then(() => {
 *             comp.baby = engine.GL.Mesh.CreateSphere('mySphere', 10, 2, engine.scene.baby);
 *             _.assign(comp.baby, meshData);
 *             return comp;
 *          });
 *     static update(engine, entity, comp, id, data) {
 *         if (!comp) {
 *             return this.create(engine, entity, id, data);
 *         }
 *         _.assign(comp.baby, data);
 *         return Promise.resolve(comp);
 *     }
 *     static dismount(engine, entity, comp) {
 *          comp.baby.dispose();
 *          delete comp.baby;
 *          return null;
 *     }
 * }
 */
/**
 * @typedef {Object} ResourceProvider
 * @property {function(id: string): EntityData} getEntity - Raw object containing default component definitions for Entity ID.
 * @property {function(id: string): ComponentClass} getComponent - Static component class for Component ID.
 */
/**
 * @typedef {function(): Promise<ResourceProvider>} ResourceLoader
 */

/**
 * @typedef {function(engine: Engine, k: string, down: bool)} KeyHandler
 */
/**
 * @typedef {Object} KeyDefinition
 * @property {string} key
 * @property {KeyHandler} [handler] - The handler function for both key up and down.
 * @property {KeyHandler} [upHandler] - The handler specifically for key up.
 * @property {KeyHandler} [downHandler] - The handler specifically for key down.
 */
/**
 * @typedef {Object} Settings
 * @property {bool} [debug=false]
 * @property {Object} [input]
 * @property {KeyDefinition[]} [input.keys]
 */

/**
 * @typedef {Object} Logger
 * @property {function} info
 * @property {function} debug
 * @property {function} warn
 * @property {function} error
 */

import {_} from './utils';
import Singleton from 'basic-singleton';
import Promise from 'bluebird';
import Scene from './scene';
import EventProxy from './state-event-proxy';
import {Events} from './state-events';

// Cannot use require.ensure when not using webpack (i.e., mocha environment)
//  so this polyfill is required to not break run.
if (typeof require.ensure !== 'function') {
    require.ensure = (dep, cb) => cb(dep);
}

let // Local vars for read-only class members
    loader,
    logger,
    playerController,
    GraphicsLibrary,
    resourceProvider,
    canvasElement,
    babylonEngine,
    terrainEntity;

/**
 * The game engine class (abstraction of BabylonJS Engine).
 *
 * @example <caption>Basic construction with default args</caption>
 * let engine = Engine.init(logger, () => Promise.resolve(resourceProvider));
 * @example <caption>One-line runner</caption>
 * Engine.init(myLogger, myResourceLazyLoader, MyPlayerCtrl, settings)
 *     .mount(document.getElementById('my-canvas'))
 *     .then(engine => engine.run(entityData))
 *     .then(engine => window.addEventListener('resize', () => engine.resize()));
 */
export default class Engine extends Singleton {

    /**
     * The general creation/initialization method for the class. Creates an
     * engine instance or returns the existing one if already instantiated.
     *
     * @param {Logger} loggerService
     * @param {ResourceLoader} resourceProviderLoader
     * @param {class} [PlayerController]
     * @param {Settings} [settings={}]
     * @return {Engine}
     */
    static init(
        loggerService,
        resourceProviderLoader,
        PlayerController,
        settings={}
    ) {
        let engine = this.instance;

        if (engine) return engine;
        loader = logger = playerController = GraphicsLibrary = terrainEntity =
            resourceProvider = canvasElement = babylonEngine = null;
        engine = new this(loggerService, PlayerController, settings);
        engine.loading = LoadStates.RES;
        loader = resourceProviderLoader().then(provider => {
            resourceProvider = provider;
            engine.loading = LoadStates.INIT;
            engine.scene = new Scene(engine);
        });
        return engine;
    }

    /**
     * For testing purposes -- cannot stub constructor
     *
     * @access private
     */
    static constructorHelper(engine, ...args) {
        engine.constructorHelper(...args);
    }

    /**
     * For testing purposes -- cannot stub constructor
     *
     * @access private
     */
    constructorHelper(loggerService, PlayerController, settings) {
        if (!PlayerController) {
            PlayerController = require('./player-controller').default;
        }
        EventProxy(this, 'engine', ['fps', 'loading']);
        /**
         * The currently rendered frames-per-second.
         *
         * @type {ProxyValue}
         */
        this.fps = 0;
        /**
         * The current loading state of the engine.
         *
         * @type {ProxyValue}
         */
        this.loading = LoadStates.BOOT;
        /** @type {?Scene} **/
        this.scene = null
        /** @type {boolean} **/
        this.running = false;
        /** @type {Settings} **/
        this.settings = settings;
        logger = loggerService;
        playerController = new PlayerController(this);
    }

    /**
     * @access private
     */
    constructor(...args) {
        super();
        Engine.constructorHelper(this, ...args);
    }

    /**
     * The BabylonJS graphics library.
     *
     * @type {?BabylonJS}
     */
    get GL() { return GraphicsLibrary; }
    /**
     * The BabylonJS.Engine instance.
     *
     * @type {?BabylonJS.Engine}
     */
    get baby() { return babylonEngine; }
    /**
     * The canvas element to which the engine has been mounted.
     *
     * @type {?DOMElement}
     */
    get canvas() { return canvasElement; }
    /**
     * @type {?ResourceProvider}
     */
    get provider() { return resourceProvider; }
    /**
     * @type {?PlayerController}
     */
    get ctrl() { return playerController; }
    /**
     * @type {Logger}
     */
    get logger() { return logger; }
    /**
     * The scene's default terrain entity.
     *
     * @type {?EntityInstance}
     */
    get terrain() { return terrainEntity; }
    /**
     * @type {EntityInstance}
     */
    set terrain(v) { return terrainEntity = v; }

    /**
     * Mount the engine to the given canvas.
     *
     * @access public
     * @param {DOMElement} canvas
     * @return {Promise<Engine>}
     */
    mount(canvas) {
        return loader
            .then(() => {
                logger.debug('Mounting engine to canvas...');
                this.loading = LoadStates.GL;
                return new Promise(res => {
                    require.ensure(['babylonjs'], () => {
                        res(require('babylonjs'));
                    }, 'graphics');
                });
            })
            .then(BabylonJS => {
                GraphicsLibrary = BabylonJS;

                this.loading = LoadStates.GAME;
                babylonEngine = new GraphicsLibrary.Engine(canvas, true);
                canvasElement = canvas;
                logger.debug('Loading scene...');
                return this.scene.mount(this);
            })
            .then(() => {
                this.loading = LoadStates.DATA;
                logger.info('Engine mounted.');
                return this;
            });
    }

    /**
     * Dismount engine from canvas.
     *
     * @access public
     * @return {Promise<Engine>}
     */
    dismount() {
        logger.debug('Dismounting from to canvas...');
        return new Promise(res => {
            if (this.running) {
                this.stop();
            }
            this.scene = this.scene.dismount(this);
            _.get(babylonEngine, 'dispose', _.noop)();
            babylonEngine = null;
            canvasElement = null;
            logger.info('Engine dismounted.');
            res(this);
        });
    }

    /**
     * Initialize given entities (if any), then begin rendering to mounted canvas.
     *
     * @access public
     * @param {EntityMetaData[]} [entities]
     * @return {Promise<Engine>}
     */
    run(entities) {
        const step = 1000 / 60;
        let count,
            time,
            accum = 0,
            defer = Promise.resolve(this);

        if (!this.running && this.loading === LoadStates.DATA) {
            if (entities) {
                this.loading = LoadStates.PROC;
                logger.debug('Adding initial entities to scene...');
                defer = this.scene.updateEntities(this, entities);
            }
            defer = defer.then(() => {
                this.ctrl.setup(this.settings);
                _.forEach(this.ctrl.DOM_EVENTS, (fn, name) =>
                    canvasElement.addEventListener(name, e =>
                        e.preventDefault() && false || this.ctrl[fn](e)));
                this.loading = LoadStates.REND;
                logger.debug('Starting engine render loop. ENGINE: ', this);
                babylonEngine.runRenderLoop(() => {
                    count = 0;
                    time = Date.now();
                    accum = babylonEngine.getDeltaTime();
                    // accum += engine.baby.getDeltaTime();
                    // while (accum >= step) {
                    //     count++;
                    //     engine.scene.tick(engine, time, step);
                    //     accum -= step;
                    //     if (count > 5) accum = 0;
                    // }
                    this.scene.tick(this, time, accum);
                    this.scene.baby.render();
                });
                this.loading = LoadStates.DONE;
                setInterval(() => {
                    this.fps = Math.floor(this.baby.fps)
                }, 500);
                this.running = true;
                return this;
            });
        }
        return defer;
    }

    /**
     * Stop the rendering loop.
     *
     * @access public
     * @return {Engine}
     */
    stop() {
        if (this.running) {
            logger.debug('Stopping engine render loop.');
            babylonEngine.stopRenderLoop();
            this.running = false;
            this.loading = LoadStates.DATA;
        }
        return this;
    }

    /**
     * Passthrough to BabylonJS.Engine#resize method.
     *
     * @access public
     * @return {undefined}
     */
    resize() {
        return _.get(babylonEngine, 'resize', _.noop)();
    }

    /**
     * Creates a new BabylonJS.Vector3.
     *
     * @access public
     * @param {Object} param
     * @param {number} [param.x=0]
     * @param {number} [param.y=0]
     * @param {number} [param.z=0]
     * @return {BabylonJS.Vector3}
     */
    toVector({x=0, y=0, z=0}={}) {
        return new this.GL.Vector3(..._.values({x, y, z}));
    }

    /**
     * Emits the given state event, only when settings.debug = true. See {@link Engine#emitEvent}.
     *
     * @access public
     * @return {undefined}
     */
    emitDebugEvent(...args) {
        return this.settings.debug ? this.emitEvent(...args) : false;
    }

    /**
     * Adds state event listener only when settings.debug = true. See {@link Engine#onEvent}.
     *
     * @access public
     * @return {undefined}
     */
    onDebugEvent(...args) {
        return this.settings.debug ? this.onEvent(...args) : false;
    }

    /**
     * Emits the given state event.
     *
     * @access public
     * @param {string} scope - The scoped event name (e.g., 'engine.fps').
     * @param {*} v - The changed value.
     * @param {Object} obj - The parent object who's property changed.
     * @return {undefined}
     */
    emitEvent(scope, v, obj) {
        return Events.emit(scope, v, obj);
    }

    /**
     * Registers a listener method for a state event.
     *
     * @access public
     * @param {string} event - The scoped event name.
     * @param {function(value: *, obj: Object)} handler - The event handler.
     * @return {undefined}
     */
    onEvent(event, handler) {
        return Events.on(event, handler);
    }

    /**
     * Hooks a Keyboard Key action listener into the engine.
     *
     * @access public
     * @param {string} key - The event key to listen for (e.g., 'w', 'a', 's', 'd', ' ', etc.).
     * @param {Object} handlers
     * @param {KeyHandler} [handlers.downHandler] - Handler method for KeyDown event.
     * @param {KeyHandler} [handlers.upHandler] - Handler method for KeyUp event.
     * @param {KeyHandler} [handlers.handler] - Handler method for both events.
     * @return {undefined}
     */
    registerKeyAction(key, {downHandler, upHandler, handler}={}) {
        const {ActionManager, ExecuteCodeAction} = this.GL;
        const kUp = ActionManager.OnKeyUpTrigger;
        const kDn = ActionManager.OnKeyDownTrigger;
        let am = this.scene.baby.actionManager;

        if (!am) {
            am = this.scene.baby.actionManager = new ActionManager(this.scene.baby);
        }
        if (handler || downHandler) {
            am.registerAction(new ExecuteCodeAction(
                {trigger: kDn, parameter: key},
                () => (handler || downHandler)(this, key, true)));
        }
        if (handler || upHandler) {
            am.registerAction(new ExecuteCodeAction(
                {trigger: kUp, parameter: key},
                () => (handler || upHandler)(this, key, false)));
        }
    }

    /**
     * Hooks Mouse action listener(s) into the engine for a given Entity.
     *
     * @access public
     * @param {Entity} entity
     * @param {Object} opts
     * @param {bool} [opts.click] - Hooks the left-click (or equivalent) when true.
     * @param {bool} [opts.altClick] - Hooks the right-click (or equivalent) when true.
     * @param {bool} [opts.over] - Hooks the mouse-over when true.
     * @param {bool} [opts.out] - Hooks the mouse-out when true.
     * @param {string} [opts.cursor] - Changes the mouse-over cursor when set.
     * @return {undefined}
     */
    registerMouseEventsForEntity(entity, {click, altClick, over, out, cursor}={}) {
        const {ActionManager, ExecuteCodeAction} = this.GL;
        const clickT = ActionManager.OnLeftPickTrigger;
        const altClickT = ActionManager.OnRightPickTrigger;
        const overT = ActionManager.OnPointerOverTrigger;
        const outT = ActionManager.OnPointerOutTrigger;

        return entity.meshAsync
            .then(mesh => mesh.ready())
            .then(({baby}) => {
                let am = baby.actionManager;

                if (!am) {
                    am = baby.actionManager = new ActionManager(this.scene.baby);
                }
                if (cursor) {
                    am.hoverCursor = cursor;
                }
                if (click) {
                    am.registerAction(new ExecuteCodeAction(
                        clickT, e => this.ctrl.entityClick(entity, e)));
                }
                if (altClick) {
                    am.registerAction(new ExecuteCodeAction(
                        altClickT, e => this.ctrl.entityAltClick(entity, e)));
                }
                if (over) {
                    am.registerAction(new ExecuteCodeAction(
                        overT, e => this.ctrl.entityOver(entity, e)));
                }
                if (out) {
                    am.registerAction(new ExecuteCodeAction(
                        outT, e => this.ctrl.entityOut(entity, e)));
                }
            });
    }

    /**
     * Removes all Mouse action listeners and mouse-over cursor for a given entity.
     *
     * @access public
     * @return {undefined}
     */
    deregisterMouseEventsForEntity(entity) {
        let am = entity.mesh.baby.actionManager;

        if (am) {
            am.hoverCursor = 'default';
            while (am.actions.length) { am.actions.pop(); }
        }
    }
}

/**
 * The various states of Engine#loading.
 *
 * @access protected
 * @type {Object}
 * @property {string} BOOT
 * @property {string} RES
 * @property {string} INIT
 * @property {string} GL
 * @property {string} GAME
 * @property {string} DATA
 * @property {string} PROC
 * @property {string} REND
 * @property {null} DONE
 */
export const LoadStates = {
    BOOT: 'Booting Up',
    RES: 'Loading Resources',
    INIT: 'Initial Setup',
    GL: 'Loading Graphics Libraries',
    GAME: 'Loading Game Engine',
    DATA: 'Waiting For Initial Data',
    PROC: 'Processing Initial Data',
    REND: 'Rendering To Canvas',
    DONE: null
};

/**
 * @access private
 */
export const getPrivateDataForTest = () => ({
    loader,
    logger,
    playerController,
    GraphicsLibrary,
    resourceProvider,
    canvasElement,
    babylonEngine,
    terrainEntity
});

import {_} from './utils';
import Singleton from 'basic-singleton';
import StateProxy from './state-event-proxy';

/**
 * @typedef {Object} Message
 * @property {string} time
 * @property {string} type
 * @property {?string} sender
 * @property {string} message
 */

let _engine;

/**
 * Controller for player logic and interactions.
 *
 * @property {number} MAX_MESSAGES - The maximum number of messages to keep in the array.
 * @example <caption>Definition</caption>
 * class MyGamePlayerController extends PlayerController {
 *     get DOM_EVENTS() {
 *         return {contextmenu: 'mAltClick', click: 'myClickMethod'};
 *     }
 *     entityOver(entity, event) {
 *         super.entityOver(entity, event);
 *         entity.MyActionableComponent.doAction();
 *     }
 *     entityClick(entity, {pointerX, pointerY}) {
 *         let {x, y, z} = this.engine.scene.baby.pick(pointerX, pointerY).pickedPoint;
 *
 *         this.player.MyOtherActionableComponent.beginMovementTo(x, y, z);
 *     }
 *     myClickMethod(event) {
 *         this.player.DestructionComponent.killAllHumans();
 *     }
 * }
 * @example <caption>Implementation</caption>
 * let engine = Engine.init(myLogger, myResourceLoader, MyGamePlayerController, {});
 */
export default class PlayerController extends Singleton {

    /**
     * Used for generating readable-timestamps in messages.
     *
     * @param {Date} [date=new Date()]
     * @return {string} - Current time in HH:mm:ss format.
     */
    static getTime(date=new Date()) {
        return `${_.padStart(date.getHours(), 2, '0')}:${_.padStart(date.getMinutes(), 2, '0')}:${_.padStart(date.getSeconds(), 2, '0')}`;
    }

    /**
     * Map of HTML DOM events to controller functions to be hooked into the window
     * when engine is mounted.
     *
     * @type {Object}
     * @example <caption>Override DOM_EVENTS</caption>
     * get DOM_EVENTS() { return {click: 'mClick'}; }
     * // Only the browser click action will trigger a Controller method.
     */
    get DOM_EVENTS() {
        return {
            click: 'mClick',
            contextmenu: 'mAltClick',
            wheel: 'mWheel',
            mouseover: 'mOver',
            mouseout: 'mOut'
        };
    }

    /**
     * @param {Engine} engine - The reference to the parent game engine.
     */
    constructor(engine) {
        super();
        StateProxy(this, `engine.ctrl`, ['messages', 'player', 'target']);
        /**
         * The list of logged game messages. New messages are inserted into the
         * beginning of the array.
         *
         * @type {Message[]}
         */
        this.messages = [];
        /**
         * The controlled player entity.
         *
         * @type {?EntityInstance}
         */
        this.player = null;
        /**
         * The currently-targeted entity (based on mouse-over).
         *
         * @type {?EntityInstance}
         */
        this.target = null;
        _engine = engine;
    }

    /**
     * Reference to the parent game engine instance.
     *
     * @type {Engine}
     */
    get engine() { return _engine; }

    /**
     * Supplies the key listeners defined in the provided game settings to
     *  {@link PlayerController#registerKeyActions}.
     *
     * @param {Settings} settings
     */
    setup(settings) {
        this.registerKeyActions(_.get(settings, 'input.keys', []));
    }

    /**
     * Hooks the provided key handlers into the game's keystroke actions.
     *
     * @param {KeyDefinition[]} keyHandlers
     */
    registerKeyActions(keyHandlers) {
        _.forEach(keyHandlers, (desc) => {
            this.engine.registerKeyAction(desc.key, desc);
        });
    }

    /**
     * Inserts new message into controller's messages array.
     *
     * @param {string} type
     * @param {?string} sender
     * @param {string} message
     * @param {number} [max=PlayerController.MAX_MESSAGES]
     * @return {Message[]} - The updated messages array.
     */
    message(type, sender, message, max=this.constructor.MAX_MESSAGES) {
        return this.messages = _.take(
            _.concat(
                {time: this.constructor.getTime(), type, sender, message},
                this.messages),
            max);
    }

    /**
     * The event handler for a mouse cursor-over event on a game entity.
     *
     * @param {EntityInstance} entity
     * @param {BabylonJS.ActionEvent} event
     */
    entityOver(entity, event) {
        if (!entity || !event) {
            throw new Error('Improper arguments passed to entityOver');
        }
        this.target = entity;
    }

    /**
     * The event handler for a mouse cursor-out event on a game entity.
     *
     * @param {EntityInstance} entity
     * @param {BabylonJS.ActionEvent} event
     */
    entityOut(entity, event) {
        if (!entity || !event) {
            throw new Error('Improper arguments passed to entityOut');
        }
        if (!this.target || this.target.uid === entity.uid) {
            this.target = null;
        }
    }

    /**
     * Example event handler for a mouse left-click event on a game entity.
     *
     * @abstract
     * @param {EntityInstance} entity
     * @param {BabylonJS.ActionEvent} event
     */
    entityClick(entity, {pointerX, pointerY}) {
        if (!entity || _.isUndefined(pointerX) || _.isUndefined(pointerY)) {
            throw new Error('Improper arguments passed to entityClick');
        }
        let {x, y, z} = this.engine.scene.baby.pick(pointerX, pointerY).pickedPoint;

        this.engine.logger.debug(`3D Click on ${entity.uid} at (${round(x)}, ${round(y)}, ${round(z)})`);
    }

    /**
     * Example event handler for a mouse right-click event on a game entity.
     *
     * @abstract
     * @param {EntityInstance} entity
     * @param {BabylonJS.ActionEvent} event
     */
    entityAltClick(entity, {pointerX, pointerY}) {
        if (!entity || _.isUndefined(pointerX) || _.isUndefined(pointerY)) {
            throw new Error('Improper arguments passed to entityAltClick');
        }
        let {x, y, z} = this.engine.scene.baby.pick(pointerX, pointerY).pickedPoint;

        this.engine.logger.debug(`3D Alt Click on ${entity.uid} at (${round(x)}, ${round(y)}, ${round(z)})`);
    }

    /**
     * Example event handler for the browser's left mouse click.
     *
     * @abstract
     * @param {SyntheticEvent} event
     * @param {number} event.pageX - The X-position of the click relative to the
     *  HTML document.
     * @param {number} event.pageY - The Y-position of the click relative to the
     *  HTML document.
     */
    mClick({pageX, pageY}) {
        if (_.isUndefined(pageX) || _.isUndefined(pageY)) {
            throw new Error('Improper event passed to mClick');
        }
        this.engine.logger.debug(`Click at (${pageX}, ${pageY})`);
    }

    /**
     * Example event handler for the browser's right mouse click.
     *
     * @abstract
     * @param {SyntheticEvent} event
     * @param {number} event.pageX - The X-position of the click relative to the
     *  HTML document.
     * @param {number} event.pageY - The Y-position of the click relative to the
     *  HTML document.
     */
    mAltClick({pageX, pageY}) {
        if (_.isUndefined(pageX) || _.isUndefined(pageY)) {
            throw new Error('Improper event passed to mAltClick');
        }
        this.engine.logger.debug(`Alt Click at (${pageX}, ${pageY})`);
    }

    /**
     * Example event handler for the browser's mouse wheel event.
     *
     * @abstract
     * @param {SyntheticEvent} event
     * @param {number} event.deltaX - The value scrolled along the X axis.
     * @param {number} event.deltaY - The value scrolled along the Y axis.
     * @param {number} event.deltaZ - The value scrolled along the Z axis.
     */
    mWheel({deltaX, deltaY, deltaZ}) {
        if (_.isUndefined(deltaY) && _.isUndefined(deltaX) && _.isUndefined(deltaX)) {
            throw new Error('Improper event passed to mWheel');
        }
        if (deltaY) {
            this.engine.logger.debug(`MouseWheel scroll ${deltaY > 0 ? 'out' : 'in'} ${deltaY}`);
        }
    }

    /**
     * Example event handler for the browser's mouse over event.
     *
     * @abstract
     * @param {SyntheticEvent} event
     * @param {number} event.pageX - The X-position of the click relative to the
     *  HTML document.
     * @param {number} event.pageY - The Y-position of the click relative to the
     *  HTML document.
     */
    mOver({pageX, pageY}) {
        if (_.isUndefined(pageX) || _.isUndefined(pageY)) {
            throw new Error('Improper event passed to mAltClick');
        }
        this.engine.logger.debug('Pointer has entered canvas element.');
    }

    /**
     * Example event handler for the browser's mouse out event.
     *
     * @abstract
     * @param {SyntheticEvent} event
     * @param {number} event.pageX - The X-position of the click relative to the
     *  HTML document.
     * @param {number} event.pageY - The Y-position of the click relative to the
     *  HTML document.
     */
    mOut({pageX, pageY}) {
        if (_.isUndefined(pageX) || _.isUndefined(pageY)) {
            throw new Error('Improper event passed to mAltClick');
        }
        this.engine.logger.debug('Pointer has left cavnas element.');
    }
