import {_} from '../../utils';
import Promise from 'bluebird';
import Static from 'basic-static';
import reducePromise from 'reduce-promise';

// List of entity keys that are not components.
const nonCompKeys = ['id', 'uid', 'tick', 'mesh', '_primaryMesh'];
// Time in MS to tick a check when waiting for entity's primary mesh to resolve.
const asyncStep = 10;
// Time in MS to wait before the primary mesh resolution times out.
const asyncTimeout = 2000;

/**
 * The Entity class
 */
export default class Entity extends Static {

    /**
     * Uses the provided metadata and the engine's resourceProvider to create a
     *  new entity.
     *
     * @param {Engine} engine
     * @param {EntityMetaData} data
     * @return {EntityInstance}
     */
    static create(engine, data) {
        const ticks = {};
        const {id, uid} = data;
        const polyData = _.merge(engine.provider.getEntity(id), data);
        const componentData = _.omit(polyData, nonCompKeys);
        const entity = Object.defineProperties({id, uid}, {
            mesh: { get: () => _.get(entity, polyData._primaryMesh) },
            tick: {
                get: () => (eng, t, dt) =>
                    _.forEach(ticks, (v, k) => { v(eng, entity, entity[k], t, dt); }),
                set: ({id, tick}) => tick ? (ticks[id] = tick) : (delete ticks[id])
            },
            meshAsync: {
                get: () => new Promise((res, rej) => {
                    const t = Date.now();
                    const stop = setInterval(() => {
                        if (entity.mesh) {
                            clearInterval(stop);
                            if (Object.getOwnPropertyDescriptor(entity, 'meshAsync').get) {
                                Object.defineProperty(entity, 'meshAsync', {
                                    value: Promise.resolve(entity.mesh),
                                    configurable: false,
                                    writable: false
                                });
                            }
                            res(entity.mesh);
                        } else if (Date.now() - t > asyncTimeout) {
                            clearInterval(stop);
                            rej(new Error('Timed out waiting for mesh of entity: ' + uid));
                        }
                    }, asyncStep);
                }),
                configurable: true
            }
        });

        return this.updateComponents(engine, entity, componentData);
    }

    /**
     * Updates/creates the component for the specified id.
     *
     * @param {Engine} engine
     * @param {EntityInstance} entity
     * @param {string} id - The component ID
     * @param {ComponentData} data - The data with which to initialize/update the component.
     * @return {Promise<Component>}
     */
    static updateComponent(engine, entity, id, data) {
        return engine.provider.getComponent(id)
            .update(engine, entity, entity[id], id, data)
            .then(comp => entity[id] = comp);
    }

    /**
     * Update/create all the components in the provided list of component data.
     *
     * @param {Engine} engine
     * @param {EntityInstance} entity
     * @param {ComponentData[]} componentData
     * @return {Promise<EntityInstance>}
     */
    static updateComponents(engine, entity, componentData) {
        return reducePromise(componentData, (v, k) => this.updateComponent(engine, entity, k, v))
            .then(() => entity);
    }

    /**
     * Dismount the specified component.
     *
     * @param {Engine} engine
     * @param {EntityInstance} entity
     * @param {string} id - The component ID.
     * @return {null}
     */
    static dismountComponent(engine, entity, id) {
        return entity[id] = engine.provider
            .getComponent(id)
            .dismount(engine, entity, entity[id]);
    }

    /**
     * Update the entity or create if it doesn't exist.
     *
     * @param {Engine} engine
     * @param {?EntityInstance} entity
     * @param {EntityMetaData} data
     * @return {Promise<EntityInstance>}
     */
    static update(engine, entity, data) {
        if (!entity) {
            return this.create(engine, data);
        }
        return this.updateComponents(engine, entity, _.omit(data, nonCompKeys));
    }

    /**
     * Dismount the entity and all of its components.
     *
     * @param {Engine} engine
     * @param {EntityInstance} entity
     * @return {null}
     */
    static dismount(engine, entity) {
        _.forEach(
            _.omit(entity, nonCompKeys),
            (v, k) => { this.dismountComponent(engine, entity, k); });
        return null;
    }

    /**
     * Execute the tick method for the provided EntityInstance.
     *
     * @param {Engine} engine
     * @param {EntityInstance} entity
     * @param {number} t
     * @param {number} dt
     */
    static tick(engine, entity, t, dt) {
        entity.tick(engine, t, dt);
    }
}

import {_} from '../utils';
import reducePromise from 'reduce-promise';
import Singleton from 'basic-singleton';
import Entity from './entity';

/**
 * The Scene class
 */
export default class Scene extends Singleton {

    constructor() {
        super();
        /**
         * The map of entities registered to the scene (keys are entity.uid).
         *
         * @type {Object}
         */
        this.children = {};
        /**
         * The active camera entity.
         *
         * @type {?EntityInstance}
         */
        this.activeCamera = null;
        Object.defineProperty(this, '_baby', {
            value: null,
            writable: true
        });
    }

    /**
     * The BabylonJS.Scene instance.
     *
     * @type {?BabylonJS.Scene}
     */
    get baby() { return this._baby; }

    /**
     * Instantiates the {@link Scene#baby} object.
     *
     * @param {Engine} engine
     */
    mount(engine) {
        this._baby = new engine.GL.Scene(engine.baby);
    }

    /**
     * Dismounts all child entities and disposes {@link Scene#baby} property.
     *
     * @param {Engine} engine
     * @return {null}
     */
    dismount(engine) {
        this.children = _.pickBy(_.mapValues(this.children, e => Entity.dismount(engine, e)));
        _.get(this, '_baby.dispose', _.noop)();
        this._baby = null;
        return null;
    }

    /**
     * Gets child entity by EntityInstance#uid.
     *
     * @param {string} uid
     * @return {EntityInstance} if entity is found.
     * @return {undefined} if entity is not found.
     */
    getEntity(uid) {
        return _.get(this.children, uid);
    }

    /**
     * Updates/creates an entity with the provided data, and saves it to {@link Scene#children}.
     *
     * @param {Engine} engine
     * @param {EntityMetaData} entityData
     * @return {Promise<EntityInstance>} - The new/updated entity.
     */
    updateEntity(engine, entityData) {
        return Entity.update(engine, this.children[entityData.uid], entityData)
            .then(e => this.children[entityData.uid] = e);
    }

    /**
     * Updates all entities in the provided list of entity data.
     *
     * @param {Engine} engine
     * @param {EntityMetaData[]} entities
     * @return {Promise<Scene>}
     */
    updateEntities(engine, entities) {
        return reducePromise(entities, e => this.updateEntity(engine, e))
            .then(() => this);
    }

    /**
     * Dismounts and removes child entity by EntityInstance#uid.
     *
     * @param {Engine} engine
     * @param {string} entityUid
     * @return {null}
     */
    removeEntity(engine, entityUid) {
        return this.children[entityUid] = Entity.dismount(engine, _.get(this.children, entityUid));
    }

    /**
     * Executes registered tick methods for all child entities.
     *
     * @param {Engine} engine
     * @param {number} t - The current timestamp in miliseconds.
     * @param {number} dt - The elapsed miliseconds since the last tick.
     */
    tick(engine, t, dt) {
        _.forEach(this.children, e => { Entity.tick(engine, e, t, dt) });
    }
}

import {_} from './utils';
import Static from 'basic-static';
import {Events} from './state-events';

/**
 * @typedef {function(scope: string, newValue: *, parentObject: Object): undefined} ScopedEventListener
 * @typedef {string} ScopedEvent - Handled by {@link ScopedEventListener}.
 */
/**
 * @typedef {Object} ProxifiedObject
 * @emits {ScopedEvent} - emits event name scoped to property change for proxied
 *  properties.
 */

/**
 * Convenience function that passes through to {@link StateEventProxy.create}.
 *
 * @return {ProxifiedObject}
 * @example <caption>Setup</caption>
 * const EventProxy = require('state-event-proxy');
 * let myObject = {};
 * @example <caption>Property Keys</caption>
 * EventProxy(myObject, 'myObject', ['myPropA', 'myPropB']);
 * console.log(myObject) // {myPropA: undefined, myPropB: undeinfed}
 * myObject.myPropA = 10; // Emits event w/ args: 'myObject.myPropA', 10, myObject
 * myObject.myPropB = {}; // Emits event w/ args: 'myObject.myPropB', {}, myObject
 * console.log(myObject) // {myPropA: 10, myPropB: {}}
 * @example <caption>Property Map</caption>
 * EventProxy(myObject, 'myObject', {
 *     myPropA: 42,
 *     myPropB: {foo: 'bar'}
 * });
 * console.log(myObject) // {myPropA: 42, myPropB: {foo: 'bar'}}
 * myObject.myPropA = 10; // Emits event w/ args: 'myObject.myPropA', 10, myObject
 * myObject.myPropB.foo = 'foo'; // Does not emit an event.
 * console.log(myObject) // {myPropA: 10, myPropB: {foo: 'foo'}}
 * @example <caption>Property Map w/ Deep Proxy</caption>
 * EventProxy(myObject, 'myObject', {
 *     myPropA: [{id: 'foo'}, {id: 'bar'}],
 *     myPropB: {a: 1, b: 2, c: 3}
 * }, {deep: true});
 * console.log(myObject) // {myPropA: [{id: 'foo'}, {id: 'bar'}], myPropB: {a: 1, b: 2, c: 3}}
 * myObject.myPropB.a = 0; // Emits event w/ args: 'myObject.myPropB.a', 0, myObject.myPropB
 * myObject.myPropB = {}; // Emits event w/ args: 'myObject.myPropB', {}, myObject
 * myObject.myPropA[0].id = 42; // Emits event w/ args: 'myObject.myPropA[0].id', 42, myObject.myPropA[0]
 * myObject.myPropA.push('foobar'); // Emits event w/ args: 'myObject.myPropA', myObject.myPropA, myObject
 * console.log(myObject) // {myPropA: [{id: 42}, {id: 'bar'}, 'foobar'], myPropB: {}}
 */
export default function EventProxy(obj, scope, properties, options={}) {
    return StateEventProxy.create(obj, scope, properties, options);
}

/**
 * @access private
 */
export const DISABLE = {};

/**
 * The StateEventProxy class.
 *
 * @access private
 */
export class StateEventProxy extends Static {

    static create(obj, scope, properties, options) {
        let self = obj || {};

        _.forEach(properties, (prop, k) => {
            let name = _.isNumber(k) ? prop : k,
                value = _.isNumber(k) ? options.def : prop;

            this.proxifyProperty(
                `${scope}.${name}`,
                obj,
                name,
                value,
                options);
        });
        return self;
    }

    static proxifyProperty(scope, obj, prop, value, {
        deep=false, enumerable=true
    }={}) {
        let p = value;

        if (deep && _.isPlainObject(value)) {
            p = this.buildProxyObject(scope, value, {deep, enumerable});
        } else if (deep && _.isArray(value)) {
            p = this.buildProxyArray(scope, obj, value, {deep, enumerable});
        }
        Object.defineProperty(obj, prop, {
            get: () => p,
            set: (v) => {
                if (deep && _.isObject(v)) {
                    v = _.isArray(v)
                        ? this.buildProxyArray(scope, obj, v, {deep, enumerable})
                        : this.buildProxyObject(scope, v, {deep, enumerable});
                }
                this.emit(scope, v, obj);
                p = v;
                return true;
            },
            configurable: true,
            enumerable
        });
    }

    static buildProxyObject(scope, obj, o) {
        let p = {};

        _.forEach(obj, (v, k) => this.proxifyProperty(`${scope}.${k}`, p, k, v, o));
        return p;
    }

    static buildProxyArray(scope, parent, arr, o) {
        const ret = new Proxy(
            _.map(arr, (e, i) =>
                _.isPlainObject(e) && this.buildProxyObject(`${scope}[${i}]`, e, o) ||
                _.isArray(e) && this.buildProxyArray(`${scope}[${i}]`, ret, e, o) || e),
            {
                get: (target, key) => {
                    if (this.isMutateFn(key)) {
                        return (...args) => {
                            DISABLE[scope] = true;
                            let v = target[key](...args);

                            delete DISABLE[scope];
                            this.emit(scope, ret, parent);
                            return v;
                        };
                    }
                    return target[key];
                },
                set: (target, i, v) => {
                    if (!isNaN(i)) {
                        v = _.isPlainObject(v) && this.buildProxyObject(`${scope}[${i}]`, v, o) ||
                            _.isArray(v) && this.buildProxyArray(`${scope}[${i}]`, ret, v, o) || v;
                        this.emit(`${scope}[${i}]`, v, ret);
                    }
                    target[i] = v;
                    return true;
                },
                deleteProperty: (target, i) => {
                    if (!isNaN(i)) {
                        this.emit(`${scope}[${i}]`, undefined, ret);
                    }
                    delete target[i];
                    return true;
                }
            }
        );

        return ret;
    }

    static emit(scope, v, obj) {
        if (!DISABLE[scope]) {
            setTimeout(() => Events.emit(scope, v, obj));
        }
    }

    static isMutateFn(key) {
        return _.includes([
            'copyWithin',
            'fill',
            'pop',
            'push',
            'reverse',
            'shift',
            'sort',
            'splice',
            'unshift'
        ], key);
    }
}

