import {_} from './utils';
import Singleton from 'basic-singleton';
import StateProxy from './state-event-proxy';

export default class PlayerController extends Singleton {

    static getTime(date=new Date()) {
        return `${_.padStart(date.getHours(), 2, '0')}:${_.padStart(date.getMinutes(), 2, '0')}:${_.padStart(date.getSeconds(), 2, '0')}`;
    }

    get DOM_EVENTS() {
        return {
            click: 'mClick',
            contextmenu: 'mAltClick',
            wheel: 'mWheel',
            mouseover: 'mOver',
            mouseout: 'mOut'
        };
    }

    constructor(engine, uiData={}) {
        super();
        StateProxy(this, `engine.ctrl`, _.assign({
            messages: [],
            player: null,
            target: null
        }, uiData));
        Object.defineProperty(this, 'engine', {
            get: () => engine
        });
    }

    setup(settings) {
        this.registerKeyActions(_.get(settings, 'input.keys', []));
    }

    registerKeyActions(keyHandlers) {
        _.forEach(keyHandlers, (desc) => {
            this.engine.registerKeyAction(desc.key, desc);
        });
    }

    message(type, sender, msg, max=this.constructor.MAX_MESSAGES) {
        return this.messages = _.take(
            _.concat(
                {time: this.constructor.getTime(), type, sender, msg},
                this.messages),
            max);
    }

    mClick({pageX, pageY}) {
        if (_.isUndefined(pageX) || _.isUndefined(pageY)) {
            throw new Error('Improper event passed to mClick');
        }
        this.engine.logger.debug(`Click at (${pageX}, ${pageY})`);
    }

    mAltClick({pageX, pageY}) {
        if (_.isUndefined(pageX) || _.isUndefined(pageY)) {
            throw new Error('Improper event passed to mAltClick');
        }
        this.engine.logger.debug(`Alt Click at (${pageX}, ${pageY})`);
    }

    mWheel({deltaX, deltaY, deltaZ}) {
        if (_.isUndefined(deltaY) && _.isUndefined(deltaX) && _.isUndefined(deltaX)) {
            throw new Error('Improper event passed to mWheel');
        }
        if (deltaY) {
            this.engine.logger.debug(`MouseWheel scroll ${deltaY > 0 ? 'out' : 'in'} ${deltaY}`);
        }
    }

    mOver({pageX, pageY}) {
        if (_.isUndefined(pageX) || _.isUndefined(pageY)) {
            throw new Error('Improper event passed to mAltClick');
        }
        this.engine.logger.debug('Pointer has entered canvas element.');
    }

    mOut({pageX, pageY}) {
        if (_.isUndefined(pageX) || _.isUndefined(pageY)) {
            throw new Error('Improper event passed to mAltClick');
        }
        this.engine.logger.debug('Pointer has left cavnas element.');
    }

    entityOver(entity, evt) {
        if (!entity || !evt) {
            throw new Error('Improper arguments passed to entityOver');
        }
        this.target = entity;
    }

    entityOut(entity, evt) {
        if (!entity || !evt) {
            throw new Error('Improper arguments passed to entityOut');
        }
        if (!this.target || this.target.uid === entity.uid) {
            this.target = null;
        }
    }

    entityClick(entity, {pointerX, pointerY}) {
        if (!entity || _.isUndefined(pointerX) || _.isUndefined(pointerY)) {
            throw new Error('Improper arguments passed to entityClick');
        }
        let {x, y, z} = this.engine.scene.baby.pick(pointerX, pointerY).pickedPoint;

        this.engine.logger.debug(`3D Click on ${entity.uid} at (${round(x)}, ${round(y)}, ${round(z)})`);
    }

    entityAltClick(entity, {pointerX, pointerY}) {
        if (!entity || _.isUndefined(pointerX) || _.isUndefined(pointerY)) {
            throw new Error('Improper arguments passed to entityAltClick');
        }
        let {x, y, z} = this.engine.scene.baby.pick(pointerX, pointerY).pickedPoint;

        this.engine.logger.debug(`3D Alt Click on ${entity.uid} at (${round(x)}, ${round(y)}, ${round(z)})`);
    }
}

PlayerController.MAX_MESSAGES = 100;

export const round = (x) => Math.floor(x * 100 + .5) / 100;

import {_} from '../../utils';
import Promise from 'bluebird';
import Static from 'basic-static';
import reducePromise from 'reduce-promise';

const nonCompKeys = ['id', 'uid', 'tick', 'mesh', '_primaryMesh'];
const asyncStep = 10;
const asyncTimeout = 2000;

export default class Entity extends Static {

    static create(engine, data) {
        const ticks = {};
        const {id, uid} = data;
        const polyData = _.merge(engine.provider.getEntity(id), data);
        const componentData = _.omit(polyData, nonCompKeys);
        const entity = Object.defineProperties({id, uid}, {
            mesh: { get: () => _.get(entity, polyData._primaryMesh) },
            tick: {
                get: () => (eng, t, dt) =>
                    _.forEach(ticks, (v, k) => { v(eng, entity, entity[k], t, dt); }),
                set: ({id, tick}) => tick ? (ticks[id] = tick) : (delete ticks[id])
            },
            get: {
                value: (path) => _.get(entity, path, false)
            },
            meshAsync: {
                get: () => new Promise((res, rej) => {
                    const t = Date.now();
                    const stop = setInterval(() => {
                        if (entity.mesh) {
                            clearInterval(stop);
                            if (Object.getOwnPropertyDescriptor(entity, 'meshAsync').get) {
                                Object.defineProperty(entity, 'meshAsync', {
                                    value: Promise.resolve(entity.mesh),
                                    configurable: false,
                                    writable: false
                                });
                            }
                            res(entity.mesh);
                        } else if (Date.now() - t > asyncTimeout) {
                            clearInterval(stop);
                            rej(new Error('Timed out waiting for mesh of entity: ' + uid));
                        }
                    }, asyncStep);
                }),
                configurable: true
            }
        });

        return this.updateComponents(engine, entity, componentData);
    }

    static updateComponent(engine, entity, id, data) {
        return engine.provider.getComponent(id)
            .update(engine, entity, entity[id], id, data)
            .then(comp => entity[id] = comp);
    }

    static updateComponents(engine, entity, componentData) {
        return reducePromise(componentData, (v, k) => this.updateComponent(engine, entity, k, v))
            .then(() => entity);
    }

    static dismountComponent(engine, entity, id) {
        return entity[id] = engine.provider
            .getComponent(id)
            .dismount(engine, entity, entity[id]);
    }

    static update(engine, entity, data) {
        if (!entity) {
            return this.create(engine, data);
        }
        return this.updateComponents(engine, entity, _.omit(data, nonCompKeys));
    }

    static dismount(engine, entity) {
        _.forEach(
            _.omit(entity, nonCompKeys),
            (v, k) => { this.dismountComponent(engine, entity, k); });
        return null;
    }

    static tick(engine, entity, t, dt) {
        entity.tick(engine, t, dt);
    }
}

import {_} from '../utils';
import reducePromise from 'reduce-promise';
import Singleton from 'basic-singleton';
import Entity from './entity';

export default class Scene extends Singleton {

    constructor() {
        super();
        Object.defineProperties(_.assign(this, {
            children: {},
            activeCamera: null
        }), {
            _baby: {
                value: null,
                writable: true
            }
        });
    }

    get baby() { return this._baby; }

    mount(engine) {
        this._baby = new engine.GL.Scene(engine.baby);
    }

    dismount(engine) {
        this.children = _.pickBy(_.mapValues(this.children, e => Entity.dismount(engine, e)));
        _.get(this, '_baby.dispose', _.noop)();
        this._baby = null;
        return null;
    }

    getEntity(uid) {
        return _.get(this.children, uid);
    }

    updateEntity(engine, entityData) {
        return Entity.update(engine, this.children[entityData.uid], entityData)
            .then(e => this.children[entityData.uid] = e);
    }

    updateEntities(engine, entities) {
        return reducePromise(entities, e => this.updateEntity(engine, e))
            .then(() => this);
    }

    removeEntity(engine, entityUid) {
        return this.children[entityUid] = Entity.dismount(engine, _.get(this.children, entityUid));
    }

    tick(engine, t, dt) {
        _.forEach(this.children, e => { Entity.tick(engine, e, t, dt) });
    }
}

import {_} from './utils';
import Static from 'basic-static';
import events from './state-events';

export default (...args) => StateEventProxy.create(...args);

export const DISABLE = {};

export class StateEventProxy extends Static {

    static create(obj, scope, properties, options) {
        let self = obj || {};

        _.forEach(properties, (prop, k) => {
            let name = _.isNumber(k) ? prop : k,
                value = _.isNumber(k) ? options.def : prop;

            this.proxifyProperty(
                `${scope}.${name}`,
                obj,
                name,
                value,
                options);
        });
        return self;
    }

    static proxifyProperty(scope, obj, prop, value, {
        deep=false, enumerable=true
    }={}) {
        let p = value;

        if (deep && _.isPlainObject(value)) {
            p = this.buildProxyObject(scope, value, {deep, enumerable});
        } else if (deep && _.isArray(value)) {
            p = this.buildProxyArray(scope, obj, value, {deep, enumerable});
        }
        Object.defineProperty(obj, prop, {
            get: () => p,
            set: (v) => {
                if (deep && _.isObject(v)) {
                    v = _.isArray(v)
                        ? this.buildProxyArray(scope, obj, v, {deep, enumerable})
                        : this.buildProxyObject(scope, v, {deep, enumerable});
                }
                this.emit(scope, v, obj);
                p = v;
                return true;
            },
            configurable: true,
            enumerable
        });
    }

    static buildProxyObject(scope, obj, o) {
        let p = {};

        _.forEach(obj, (v, k) => this.proxifyProperty(`${scope}.${k}`, p, k, v, o));
        return p;
    }

    static buildProxyArray(scope, parent, arr, o) {
        const ret = new Proxy(
            _.map(arr, (e, i) =>
                _.isPlainObject(e) && this.buildProxyObject(`${scope}[${i}]`, e, o) ||
                _.isArray(e) && this.buildProxyArray(`${scope}[${i}]`, ret, e, o) || e),
            {
                get: (target, key) => {
                    if (this.isMutateFn(key)) {
                        return (...args) => {
                            DISABLE[scope] = true;
                            let v = target[key](...args);

                            delete DISABLE[scope];
                            this.emit(scope, ret, parent);
                            return v;
                        };
                    }
                    return target[key];
                },
                set: (target, i, v) => {
                    if (!_.isNaN(i)) {
                        v = _.isPlainObject(v) && this.buildProxyObject(`${scope}[${i}]`, v, o) ||
                            _.isArray(v) && this.buildProxyArray(`${scope}[${i}]`, ret, v, o) || v;
                        this.emit(`${scope}[${i}]`, v, ret);
                    }
                    target[i] = v;
                    return true;
                },
                deleteProperty: (target, i) => {
                    if (!_.isNaN(i)) {
                        this.emit(`${scope}[${i}]`, undefined, ret);
                    }
                    delete target[i];
                    return true;
                }
            }
        );

        return ret;
    }

    static emit(scope, v, obj) {
        if (!DISABLE[scope]) {
            setTimeout(() => events.emit(scope, v, obj));
        }
    }

    static isMutateFn(key) {
        return _.includes([
            'copyWithin',
            'fill',
            'pop',
            'push',
            'reverse',
            'shift',
            'sort',
            'splice',
            'unshift'
        ], key);
    }
}

import EventEmitter from 'events';

const events = new EventEmitter();

export default events;
